'use client';

import Image from 'next/image';
import { Button } from '@/components/ui/button';
import { useState } from 'react';

const tabs = ['Posts', 'About', 'Friends', 'Photos'];

export default function ProfilePage() {
  const [activeTab, setActiveTab] = useState('Posts');

  return (
    <div className="bg-gray-100 min-h-screen">
      {/* Cover Section */}
      <div className="relative h-60 sm:h-72 bg-white shadow-md">
        <Image
          src="/images/cover.jpg"
          alt="Cover Photo"
          layout="fill"
          objectFit="cover"
          className="rounded-b-xl"
        />
        <div className="absolute bottom-[-50px] left-4 sm:left-10">
          <Image
            src="/images/profile.jpg"
            alt="Profile"
            width={100}
            height={100}
            className="rounded-full border-4 border-white"
          />
        </div>
      </div>

      {/* Profile Info */}
      <div className="bg-white pt-16 pb-6 px-4 sm:px-10 shadow">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
          <div>
            <h1 className="text-2xl font-bold">Rahish Sheikh</h1>
            <p className="text-gray-600 text-sm">Texas College of Management & IT</p>
          </div>
          <div className="mt-4 sm:mt-0 flex gap-2">
            <Button variant="default">Add Friend</Button>
            <Button variant="outline">Message</Button>
          </div>
        </div>

        {/* Tabs */}
        <div className="mt-6 border-b border-gray-200 flex gap-6 overflow-x-auto text-sm sm:text-base">
          {tabs.map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`pb-2 ${
                activeTab === tab
                  ? 'border-blue-500 text-blue-600 font-medium border-b-2'
                  : 'text-gray-500 hover:text-blue-500'
              }`}
            >
              {tab}
            </button>
          ))}
        </div>
      </div>

      {/* Tab Content */}
      <div className="p-4 sm:p-8 max-w-4xl mx-auto">
        {activeTab === 'Posts' && (
          <div className="grid gap-4">
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="font-semibold text-gray-800">My First Post</h3>
              <p className="text-gray-600 mt-2">Excited to join Texas College ðŸŽ“</p>
            </div>
            <div className="bg-white rounded-lg shadow p-4">
              <h3 className="font-semibold text-gray-800">Event Update</h3>
              <p className="text-gray-600 mt-2">Don't miss the management seminar on Friday.</p>
            </div>
          </div>
        )}

        {activeTab === 'About' && (
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-xl font-bold mb-4">About</h3>
            <p><strong>College:</strong> Texas International College</p>
            <p><strong>Location:</strong> Kathmandu, Nepal</p>
            <p><strong>Studying:</strong> BIT (Bachelor in Information Technology)</p>
          </div>
        )}

        {activeTab === 'Friends' && (
          <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="bg-white rounded-lg p-3 shadow text-center">
                <Image
                  src={`/images/friend${i + 1}.jpg`}
                  alt="Friend"
                  width={80}
                  height={80}
                  className="rounded-full mx-auto"
                />
                <p className="mt-2 font-medium text-gray-700">Friend {i + 1}</p>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'Photos' && (
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
            {[...Array(6)].map((_, i) => (
              <Image
                key={i}
                src={`/images/photo${i + 1}.jpg`}
                alt={`Photo ${i + 1}`}
                width={300}
                height={300}
                className="rounded-lg object-cover h-48 w-full"
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
