+// components/AsideBar.tsx
+'use client';
+
+import Link from 'next/link';
+import { useState } from 'react';
+import { HiX } from 'react-icons/hi';
+import { cn } from '@/lib/utils';
+
+interface AsideBarProps {
+  isOpen: boolean;
+  onClose: () => void;
+}
+
+export default function AsideBar({ isOpen, onClose }: AsideBarProps) {
+  return (
+    <>
+      {/* Overlay */}
+      {isOpen && (
+        <div
+          className="fixed inset-0 bg-black bg-opacity-30 z-40"
+          onClick={onClose}
+        />
+      )}
+
+      {/* Sidebar */}
+      <div
+        className={cn(
+          'fixed top-0 right-0 h-full w-64 bg-white shadow-lg z-50 transform transition-transform duration-300 ease-in-out',
+          isOpen ? 'translate-x-0' : 'translate-x-full'
+        )}
+      >
+        <div className="flex justify-between items-center px-4 py-3 border-b">
+          <h2 className="text-lg font-semibold">Account</h2>
+          <button onClick={onClose} className="text-gray-600 hover:text-red-500">
+            <HiX className="text-2xl" />
+          </button>
+        </div>
+
+        <div className="p-4 flex flex-col space-y-4">
+          <Link href="/login" onClick={onClose} className="border border-blue-600 text-blue-600 text-center py-2 rounded hover:bg-blue-50 transition">Login</Link>
+          <Link href="/signup" onClick={onClose} className="bg-blue-600 text-white text-center py-2 rounded hover:bg-blue-700 transition">Sign Up</Link>
+          <Link href="/profile" onClick={onClose} className="text-sm text-gray-600 hover:text-blue-600 transition">My Profile</Link>
+          <Link href="/order-history" onClick={onClose} className="text-sm text-gray-600 hover:text-blue-600 transition">Order History</Link>
+          <Link href="/settings" onClick={onClose} className="text-sm text-gray-600 hover:text-blue-600 transition">Settings</Link>
+          <Link href="/support" onClick={onClose} className="text-sm text-gray-600 hover:text-blue-600 transition">Support</Link>
+          <Link href="/logout" onClick={onClose} className="text-sm text-red-500 hover:text-red-600 transition">Logout</Link>
+        </div>
+      </div>
+    </>
+  );
+}

